@import url("https://fonts.googleapis.com/css2?family=Open+Sans:wght@300;400&display=swap");

* {
  box-sizing: border-box;
  padding: 0;
  margin: 0;
}

body {
  font-family: "Open Sans", sans-serif;
  background: #fff;
  color: #333;
  line-height: 1.6;
}

ul {
  list-style: none;
}

.btn {
  text-decoration: none;
}

p {
  margin: 10px 0;
  font-weight: 300;
  line-height: 1.2;
}

img {
  width: 100%;
}

/* Navbar */
.navbar {
  display: flex;
  flex-wrap: wrap;
  align-items: center;
  justify-content: space-between;
  background-color: rgb(36, 36, 36);
  color: #fff;
  opacity: 0.8;
  width: 100%;
  position: fixed;
  top: 0px;
  padding: 0 30px;
}

.navbar {
  z-index: 10;
}

.navbar a {
  color: #fff;
  padding: 10px 20px;
  margin: 5px;
  text-decoration: none;
}

.navbar a:hover {
  border-bottom: #28a745 2px solid;
  background-color: rgb(128, 128, 128);
}

.navbar a:hover {
  transition: 0.5s;
}

.navbar ul {
  display: flex;
}

.navbar .logo {
  font-weight: 400;
}

.hamburger {
  background-color: transparent;
  border: 0;
  display: none;
  color: #fff;
  font-size: 20px;
  cursor: pointer;
}

.hamburger:focus {
  outline: none;
}

.hamburger i:hover {
  border-bottom: #28a745 2px solid;
  background-color: gray;
}

.hamburger i:hover {
  transition: 0.5s;
}

/* Header */

.hero {
  background-image: url("https://media.defense.gov/2014/Jul/16/2000832464/780/780/0/140715-F-MI136-027.JPG");
  background-repeat: no-repeat;
  background-position: center;
  background-size: cover;
  height: 100vh;
  position: relative;
  color: #fff;
}

.hero::before {
  content: "";
  position: absolute;
  top: 0;
  left: 0;
  width: 100%;
  height: 100%;
  background: rgba(0, 0, 0, 0.6);
}

.hero * {
  z-index: 10;
}

/* Home */

.hero .homeContainer {
  display: flex;
  flex-direction: column;
  align-items: center;
  justify-content: center;
  text-align: center;
  height: 100%;
}

.hero .home p {
  font-size: 20px;
  max-width: 600px;
  margin: auto;
  font-weight: 400;
}

.hero .home h1 {
  font-size: 15px;
  max-width: 600px;
  margin: 20px 0 30px;
  font-weight: 50;
}

/* Contact */

.hero .contactContainer {
  display: flex;
  flex-direction: column;
  align-items: center;
  justify-content: center;
  text-align: center;
  height: 100%;
}

.hero .contact h2 {
  font-size: 25px;
  max-width: 600px;
  margin: auto;
  font-weight: 500;
}

.hero .contact p {
  font-size: 20px;
  max-width: 600px;
  margin: 20px 0 30px;
  font-weight: 400;
}

.hero .contact hr {
  overflow: visible;
  height: 30px;
  border-style: solid;
  border-color: #fff;
  border-width: 1px 0 0 0;
  border-radius: 20px;
}

a {
  color: #fff;
}

/* about */

.hero .aboutContainer {
  display: flex;
  flex-direction: column;
  align-items: center;
  justify-content: center;
  text-align: center;
  height: 100%;
}

.hero .about h2 {
  font-size: 25px;
  max-width: 600px;
  margin: auto;
  font-weight: 500;
}

.hero .about p {
  font-size: 20px;
  max-width: 600px;
  margin: 20px 0 30px;
  font-weight: 400;
}

.hero .about hr {
  overflow: visible;
  height: 30px;
  border-style: solid;
  border-color: #fff;
  border-width: 1px 0 0 0;
  border-radius: 20px;
}

/* login */

.hero .loginContainer {
  display: flex;
  flex-direction: column;
  align-items: center;
  justify-content: center;
  text-align: center;
  height: 100%;
}

.hero .login h2 {
  font-size: 25px;
  max-width: 600px;
  margin: auto;
  font-weight: 500;
}

.hero .login hr {
  overflow: visible;
  height: 30px;
  border-style: solid;
  border-color: #fff;
  border-width: 1px 0 0 0;
  border-radius: 20px;
}

.loginGroup {
  width: 100%;
  margin-top: 0px;
}
.loginGroup input {
  width: 100%;
  padding: 5px;
}

/* Register */

.hero .register {
  display: flex;
  flex-direction: column;
  align-items: center;
  justify-content: center;
  text-align: center;
  height: 100vh;
  padding: 100px;
  line-height: 2.5;
}

.hero .register h2 {
  font-size: 25px;
  max-width: 600px;
  margin: auto;
  font-weight: 500;
}

.hero .register hr {
  overflow: visible;
  height: 30px;
  border-style: solid;
  border-color: #fff;
  border-width: 1px 0 0 0;
  border-radius: 20px;
  width: 100%;
}

.registerGroup {
  display: flex;
  height: 30px;
  justify-content: space-between;
  margin: 10px 15px;
  width: 93%;
}

/* max-width 800px */

@media screen and (max-width: 800px) {
  .navbar ul {
    display: none;
    width: 100%;
    flex-direction: column;
  }
  .navbar ul.show {
    display: flex;
  }
  .navbar a:hover {
    border: #28a745 1px solid;
    border-radius: 10px;
    padding: 5px 5px;
  }

  .navbar a:hover {
    transition: 0.8s;
  }

  .hamburger {
    display: block;
  }
  .btn {
    width: 100%;
  }
  .logo {
    width: 100%;
  }
  .navbar {
    padding: 0px;
  }
}

#status {
  width: 100%;
  max-width: 500px;
  margin-top: 10px;
  text-align: center;
  padding: 0px;
  border-radius: 8px;
}

#status.success {
  background-color: #28a745;
  animation: status 4s ease forwards;
}
#status.error {
  background-color: rgb(245, 25, 25);
  animation: status 4s ease forwards;
}

@keyframes status {
  0% {
    opacity: 1;
    pointer-events: all;
  }
  90% {
    opacity: 1;
    pointer-events: all;
  }
  100% {
    opacity: 0;
    pointer-events: none;
  }
}
